import ApolloClient from "apollo-boost";

const client = new ApolloClient({
  uri: "http://localhost:3000/admin/api"
});
import gql from "graphql-tag";

const GET_DOGS = gql`
  query {
    allPosts(first: 10) {
      url
    }
  }
`;

function Page({ posts }) {
  return (
    <div>
      {posts.map(post => (
        <div>{post.url}</div>
      ))}
    </div>
  );
}

Page.getInitialProps = async ({ req }) => {
  const res = await client.query({
    query: GET_DOGS
  });
  return res.data;
};

export default Page;
