import ApolloClient from "apollo-boost";
import Link from "next/link";

const client = new ApolloClient({
  uri: "http://localhost:3000/admin/api"
});
import gql from "graphql-tag";

const GET_DOGS = gql`
  query {
    allPosts(first: 10) {
      url
      title
      description
      image {
        publicUrl
      }
    }
  }
`;

const Page = posts => (
  <div>
    <h1>Posts</h1>
    {posts.allPosts.map(post => (
      <div key={post.id}>
        <img src={post.image.publicUrl} alt={post.title}></img>
        <Link href={post.url}>
          <a>{post.title}</a>
        </Link>
      </div>
    ))}
    <style jsx>{`
      img {
        max-width: 200px;
      }
    `}</style>
  </div>
);

Page.getInitialProps = async () => {
  const res = await client.query({
    query: GET_DOGS
  });
  return res.data;
};

export default Page;
